import React, { Component } from 'react'
import { Descriptions, Divider, Breadcrumb, Button, Input, message, InputNumber, DatePicker, Table, ConfigProvider, Spin, List, Space, Affix } from 'antd';
import { BrowserRouter, Link, Switch, Route } from 'react-router-dom'
import { nanoid } from 'nanoid'
import reqwest from 'reqwest';
import InfiniteScroll from 'react-infinite-scroller';
import { SearchOutlined } from '@ant-design/icons'
import './index.css'
// 配置中文环境
import zh_CN from 'antd/lib/locale-provider/zh_CN';
import 'moment/locale/zh-cn';

const { RangePicker } = DatePicker;

const columns = [
    { title: 'cateid', dataIndex: 'cateid', key: 'cateid' },
    { title: 'category', dataIndex: 'category', key: 'category' },
];

const getRandomuserParams = params => ({
    results: params.pagination.pageSize,
    page: params.pagination.current,
    ...params,
});


export default class Om extends Component {

    state = {
        date: [],
        data: [],
        pagination: {
            current: 1,
            pageSize: 10,
        },
        loading: false,
    };

    dateOnChange = (value, dateString) => {
        console.log('Formatted Selected Time: ', dateString);
        this.setState({
            date: dateString
        })
    }

    OmSearch = () => {
        console.log(this.state.date)
        let [oid, ofName, date] = [
            this.idInput.value,
            this.nameInput.state.value,
            this.state.date
        ]
        const data = { oid, ofName, date }
        console.log(data)
        reqwest({
            url: 'searchorderform',
            method: 'post',
            type: 'json',
            data: data
        })
    }

    handleTableChange = (pagination) => {
        console.log(pagination)
        let { current, pageSize } = pagination
        // console.log(pagination)
        this.setState({
            pagination: {
                current,
                pageSize
            }
        })
        this.fetch({
            pagination,
        });
    };

    fetch = (params = {}) => {
        this.setState({ loading: true });
        reqwest({
            // 后端接口
            url: '/userlist',
            method: 'post',
            type: 'json',
            // 传递给后端的数据
            data: getRandomuserParams(params),
        })
            .then(data => {
                this.setState({
                    loading: false,
                    // 根据接口返回的数据源
                    data: data.results,
                    pagination: {
                        ...params.pagination,
                        // 根据接口返回的总条数
                        total: data.total,
                    },
                });
            })
            ;
    };


    componentDidMount() {
        const { pagination } = this.state;
        this.fetch({ pagination });
    }

    render() {
        const { data, pagination, loading } = this.state;
        return (
            <div>
                <Divider style={{ margin: 0 }} />
                <div className="Om-head">
                    <div className="Om-desc-wraper">
                        <Breadcrumb className="bdc">
                            <Breadcrumb.Item>
                                <a href="/home">主页</a>
                            </Breadcrumb.Item>
                            <Breadcrumb.Item>订单信息管理</Breadcrumb.Item>
                        </Breadcrumb>
                        <Descriptions title="订单管理" className="desc">
                            <Descriptions.Item>展示订单信息，查询订单，查看订单状态，处理订单的发货，确认送达和退款</Descriptions.Item>
                        </Descriptions>
                    </div>
                    <div className="status-switch">
                        <Space>
                            <Button type="primary" className="status-button sendgoods">待发货</Button>
                            <Button type="primary" className="status-button delivery">配送中</Button>
                            <Button type="primary" className="status-button success">已成功</Button>
                            <Button type="primary" className="status-button refund">退款中</Button>
                            <Button type="primary" className="status-button refunded">已退款</Button>
                        </Space>
                    </div>
                </div>
                <Affix>
                    <div className="search">
                        <Divider style={{ margin: 0 }} />
                        <Space style={{ marginTop: 20 }} size={20}>
                            <div>
                                订单id:
                                <InputNumber name='Oid' className="Om-input" ref={elev => { this.idInput = elev }} />
                            </div>
                            <div>
                                用户名称:
                                <Input name="commodity" className="Om-input" ref={elev => { this.nameInput = elev }} />
                            </div>
                            <div>
                                时间:
                                <ConfigProvider locale={zh_CN}>
                                    <RangePicker
                                        showTime={{ format: 'HH:mm' }}
                                        format="YYYY-MM-DD HH:mm"
                                        onChange={this.dateOnChange}
                                        ref={elev => { this.dateElev = elev }}
                                    />
                                </ConfigProvider>
                            </div>
                            <div style={{ marginLeft: 20 }}>
                                <Button type="primary" shape="circle" icon={<SearchOutlined />} onClick={this.OmSearch} />
                            </div>
                        </Space>
                    </div>
                </Affix>
                <div className="contentWraper">
                    {/* <Table
                        columns={columns}
                        expandable={{
                            expandedRowRender: record => <p style={{ margin: 0 }}>{record.description}</p>,
                        }}
                        dataSource={this.state.data}
                        pagination={{
                            pageSize: 100000,
                            hideOnSinglePage: true,
                            responsive: true
                        }}
                        scroll={{ y: 410 }}
                    /> */}
                    <Table
                        columns={this.columns}
                        rowKey={record => record.login}
                        dataSource={data}
                        pagination={pagination}
                        loading={loading}
                        onChange={this.handleTableChange}
                    />

                </div>
            </div>

        )
    }
}
