import React, { Component } from 'react';
import { Form, Input, Button, Checkbox } from 'antd';
import 'antd/dist/antd.css';
import './index.css';


const layout = {
    labelCol: { span: 10 },
    wrapperCol: { span: 50 },
};
const tailLayout = {
    wrapperCol: { offset: 0, span: 10 },
};

export default class Login extends Component {

    submitHandle = () => {
        console.log(this.nameElem)
        console.log(this.nameElem.props.value)
        console.log(this.passwordElem.props.value)
    }

    render() {
        return (
            <div id="pageLogin" >
                <div id="formWrap">
                    <p  style={{color:'white',fontSize:17}}>请使用您的账号密码登录系统</p>
                    <Form
                        initialValues={{ remember: true }}
                        {...layout}
                    >
                        <Form.Item
                            name="username"
                            rules={[{ required: true, message: '请输入你的用户名！' }]}
                        >
                            <Input ref={input => {this.nameElem = input}} value="2" />
                        </Form.Item>

                        <Form.Item
                            name="password"
                            rules={[{ required: true, message: '请输入你的密码！' }]}
                        >
                            <Input.Password  ref={input => {this.passwordElem = input}}/>
                        </Form.Item>

                        <Form.Item {...tailLayout} name="remember" valuePropName="checked">
                            <Checkbox  style={{color:'white'}}>记住密码</Checkbox>
                        </Form.Item>

                        <Form.Item {...tailLayout}>
                            <Button type="primary" htmlType="submit" id="submit" onClick={this.submitHandle}>
                                登陆
                            </Button>
                        </Form.Item>
                    </Form>
                    <p style={{color:'white'}}>CY-Supermarket-Management-System</p>
                    <p style={{color:'white'}}>Designed By LTH</p>
                </div>

            </div>
        )
    }
}
