import React, { Component } from 'react'
import { BrowserRouter, Route, Switch, Redirect, Link } from 'react-router-dom';
import { Descriptions, Divider, Breadcrumb, Button, message, Popconfirm, Table, Tag, Space, Image } from 'antd';
import reqwest from 'reqwest';
import 'antd/dist/antd.css'
import './index.css'

export default class Adm extends Component {
    state = {
        adData: []
    }

    confirm(ID) {
        const data = { ID: ID }
        reqwest({
            // 后端接口
            url: '/deletead',
            method: 'post',
            type: 'json',
            // 传递给后端的数据
            data: data,
        })
            .then(res => {
                console.log(res)
                this.setState({
                    adData: res.re
                },() => {
                    message.success('删除成功');
                })
                
            }, () => {
                message.error('删除失败');
            })
    }

    columns = [
        {
            title: 'ID',
            dataIndex: 'ID',
            key: 'ID',
            defaultSortOrder: 'ascend',
            sorter: (a, b) => a.ID - b.ID,
        },
        {
            title: '广告公司',
            dataIndex: 'adCompany',
            key: 'adCompany',
        },
        {
            title: '广告名称',
            dataIndex: 'adName',
            key: 'adName',
        },
        {
            title: '所属分类',
            dataIndex: 'category',
            key: 'category',
        },
        {
            title: '图片',
            key: 'picture',
            dataIndex: 'picture',
            render: src => (
                <>
                    <Image
                        preview={false}
                        width={150}
                        src={src}
                    />
                </>
            ),
        },
        {
            title: '操作',
            key: 'ID',
            render: (ID) => {
                return (
                    <>
                        <Button
                            type="primary"
                            style={{ borderRadius: 5 }}
                        >
                            修改图片
                        </Button>
                        <Button
                            type="primary"
                            style={{ borderRadius: 5, marginLeft: 20 }}
                            danger
                        >
                            <Popconfirm
                                title="确定删除该用户吗?"
                                onConfirm={this.confirm.bind(this, ID)}
                                okText="确认"
                                cancelText="取消"
                            >
                                删除广告
                        </Popconfirm>
                        </Button>
                    </>
                )
            }
        },
    ];

    componentDidMount() {
        reqwest({
            url: '/adlist',
            method: 'get',
            type: 'json',
        })
            .then(res => {
                this.setState({
                    adData: res
                })
            })
    }

    render() {
        return (
            <div>
                <Divider style={{ margin: 0 }} />
                <div className="descwraper">
                    <Breadcrumb className="bdc">
                        <Breadcrumb.Item>
                            <a href="/home">主页</a>
                        </Breadcrumb.Item>
                        <Breadcrumb.Item>广告投放管理</Breadcrumb.Item>
                    </Breadcrumb>
                    <Descriptions title="广告管理" className="desc">
                        <Descriptions.Item>广告信息展示，可以进行新增广告，更改广告，删除广告操作。注意：最多只允许3个广告位</Descriptions.Item>
                    </Descriptions>
                </div>
                <div className="contentWraper">
                    {/* <BrowserRouter>
                        <Switch>

                        </Switch>
                    </BrowserRouter> */}
                    <Table columns={this.columns} dataSource={this.state.adData} pagination={false} />
                </div>
            </div>
        )
    }
}
