import React, { Component } from 'react'
import { Card, Col, Row } from 'antd';
import { Chart } from '@antv/g2';
import './index.css';



export default class Dashboard extends Component {

    componentDidMount() {

        const data = [
            { item: '事例一', count: 40, percent: 0.4 },
            { item: '事例二', count: 21, percent: 0.21 },
            { item: '事例三', count: 17, percent: 0.17 },
            { item: '事例四', count: 13, percent: 0.13 },
            { item: '事例五', count: 9, percent: 0.09 },
        ];

        const chart = new Chart({
            container: 'container',
            autoFit: true,
            height: 500,
        });
        chart.coordinate('theta', {
            radius: 0.75,
        });

        chart.data(data);

        chart.scale('percent', {
            formatter: (val) => {
                val = val * 100 + '%';
                return val;
            },
        });

        chart.tooltip({
            showTitle: false,
            showMarkers: false,
        });

        chart
            .interval()
            .position('percent')
            .color('item')
            .label('percent', {
                content: (data) => {
                    return `${data.item}: ${data.percent * 100}%`;
                },
            })
            .adjust('stack');

        chart.interaction('element-active');

        chart.render();

    }

    render() {
        return (
            <div className="contentWraper">
                <div className="site-card-wrapper ">
                    <Row gutter={16}>
                        <Col span={6}>
                            <Card
                                className="data-card"
                                title={`今日新增：${341}元`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总销售额：<br />
                                    <div className='Card_data'>
                                        {`3400`}元
                                </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card
                                title={`今日新增：${6}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总待发货数：<br />
                                    <div className='Card_data'>
                                        {`32`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card
                                title={`今日新增：${1}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总退款数：<br />
                                    <div className='Card_data'>
                                        {`40`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card
                                title={`今日新增：${10}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    成交笔数：<br />
                                    <div className='Card_data'>
                                        {`400`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                    </Row>
                </div>
                <div id="container"></div>
            </div>

        )
    }
}
