import React, { Component } from 'react'
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom';
import { Layout, Menu, Breadcrumb } from 'antd';
import './index.css';
import {
    MenuUnfoldOutlined,
    MenuFoldOutlined,
    UserOutlined,
    AppstoreOutlined,
    AlignLeftOutlined,
    PieChartOutlined,
    FileTextOutlined,
    AccountBookOutlined
} from '@ant-design/icons';

const { Header, Sider, Content } = Layout;
const { SubMenu } = Menu;

export default class Home extends Component {
    state = {
        collapsed: false,
    };

    toggle = () => {
        this.setState({
            collapsed: !this.state.collapsed,
        });
    };

    render() {
        let adminObj = JSON.parse(sessionStorage.getItem("admin"))
        return (adminObj === null) ? <Redirect path='/login' /> :
            (
                <Layout>
                    <Sider id='sider' trigger={null} collapsible collapsed={this.state.collapsed}>
                        <div className="logo" style={{ color: 'white', textAlign: 'center', padding: 19 }}>
                            重邮超市管理系统
                        </div>
                        <Menu id="menu" theme="dark" mode="inline" defaultSelectedKeys={['1']}>
                            <Menu.Item key="1" icon={<PieChartOutlined />}>
                                数据概况
                            </Menu.Item>
                            <Menu.Item key="2" icon={<UserOutlined />}>
                                用户信息管理
                            </Menu.Item>
                            <Menu.Item key="3" icon={<AppstoreOutlined />}>
                                商品信息管理
                            </Menu.Item>
                            <SubMenu key="4" icon={<AlignLeftOutlined />} title="商品分类管理">
                                <Menu.Item key="sub1" >
                                    一级分类
                                </Menu.Item>
                                <Menu.Item key="sub2" >
                                    二级分类
                                </Menu.Item>
                            </SubMenu>
                            <SubMenu key="5" icon={<FileTextOutlined />} title="订单信息管理">
                                <Menu.Item key="sub3" >
                                    订单查询
                                </Menu.Item>
                                <Menu.Item key="sub4" >
                                    订单配送
                                </Menu.Item>
                                <Menu.Item key="4" >
                                    退款处理
                                </Menu.Item>
                            </SubMenu>
                            <Menu.Item key="6" icon={<AccountBookOutlined />}>
                                广告投放管理
                            </Menu.Item>
                        </Menu>
                    </Sider>
                    <Layout className="site-layout">
                        <Header className="site-layout-background" style={{ paddingLeft: 16 }}>
                            {React.createElement(this.state.collapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
                                className: 'trigger',
                                onClick: this.toggle,
                            })}
                        </Header>
                        <Content
                            className="site-layout-background"
                            style={{
                                margin: '24px 16px',
                                padding: 24,
                                minHeight: 610,
                            }}
                        >
                            Content
                    </Content>
                    </Layout>
                </Layout>
            )
    }
}
