import React, { Component } from 'react';
import { Form, Input, Button, Checkbox, message } from 'antd';
import 'antd/dist/antd.css';
import './index.css';
import { Redirect } from 'react-router';
import axios from 'axios'

const layout = {
    labelCol: { span: 10 },
    wrapperCol: { span: 50 },
};
const tailLayout = {
    wrapperCol: { offset: 0, span: 10 },
};

export default class Login extends Component {
    constructor(props) {
        super(props)
        this.state = {
            admin: {
                // 根据登陆验证接口，来写形式
                name: '',
                password: ''
            },
            status:''
        }
    }

    submitHandle = () => {
        // console.log(this.nameElem.props.value)
        // console.log(this.passwordElem.props.value)
        if (this.nameElem.props.value !== undefined && this.passwordElem.props.value !== undefined) {
            let admin = this.state.admin
            admin.name = this.nameElem.props.value
            admin.password = this.passwordElem.props.value
            this.setState({
                admin
            })
            axios.post('/login', this.state.admin)
                .then(() => {
                    // this.setState({
                    //     status
                    // })
                    console.log(this.state.admin)
                    let adminObj = JSON.stringify(this.state.admin)
                    console.log(adminObj)
                    sessionStorage.setItem("admin", adminObj)
                    window.location.href = "./home"
                })
        } else {


            message.success("登陆成功！")

            message.error("登陆失败")
        }
    }

    render() {
        return (
            <div id="pageLogin" >
                <div id="formWrap">
                    <p style={{ color: 'white', fontSize: 18 }}>请使用您的账号密码登录系统</p>
                    <Form
                        initialValues={{ remember: true }}
                        {...layout}
                    >
                        <Form.Item
                            name="username"
                            rules={[{ required: true, message: '请输入你的用户名！' }]}
                        >
                            <Input ref={input => { this.nameElem = input }} value="2" style={{ borderRadius: 7 }} />
                        </Form.Item>

                        <Form.Item
                            name="password"
                            rules={[{ required: true, message: '请输入你的密码！' }]}
                        >
                            <Input.Password ref={input => { this.passwordElem = input }} style={{ borderRadius: 7 }} />
                        </Form.Item>

                        <Form.Item {...tailLayout} name="remember" valuePropName="checked">
                            <Checkbox style={{ color: 'white' }}>记住密码</Checkbox>
                        </Form.Item>

                        <Form.Item {...tailLayout}>
                            <Button type="primary" htmlType="submit" id="submit" onClick={this.submitHandle} style={{ borderRadius: 7 }}>
                                登陆
                            </Button>
                        </Form.Item>
                    </Form>
                    <p style={{ color: 'white' }}>CY-Supermarket-Management-System</p>
                    <p style={{ color: 'white' }}>Designed By LTH</p>
                </div>

            </div>
        )
    }
}
