import React, { Component } from 'react'
import { Card, Col, Row, Space, Button } from 'antd';
import { Chart } from '@antv/g2';
import reqwest from 'reqwest'
import './index.css';

export default class Dashboard extends Component {

    state = {
        results: {

        }
    }

    // 将订单信息中的状态切换成中文
    comfirmStatusLanguage = (arr) => {
        arr.forEach(item => {
            // console.log(item.status)
            switch (item.item) {
                case "willdelivery":
                    item.item = "待发货"
                    break;
                case "delivery":
                    item.item = "配送中"
                    break;
                case "success":
                    item.item = "已成交"
                    break;
                case "refund":
                    item.item = "退款中"
                    break;
                case "refunded":
                    item.item = "已退款"
                    break;
                case "totalSuccess":
                    item.item = "总成交数"
                    break;
                case "totalRefunded":
                    item.item = "总失败数"
                    break;
            }
        })
    }

    componentDidMount() {
        reqwest({
            url: "/dashboard/showdashboard",
            type: "json",
            method: "get",
        })
            .then(res => {
                console.log(res)
                this.comfirmStatusLanguage(res.results.ofSummary)
                comfirmStatusLanguage
                this.setState({
                    results: res.results
                }, () => {
                    const results = this.state.results
                    let timer1 = setTimeout(() => {
                        const data = results.ofSummary;

                        const chart = new Chart({
                            container: 'order-from-details',
                            autoFit: true,
                            height: 300,
                            width: 630
                        });

                        chart.coordinate('theta', {
                            radius: 0.75,
                        });

                        chart.data(data);

                        chart.scale('percent', {
                            formatter: (val) => {
                                val = val * 100 + '%';
                                return val;
                            },
                        });

                        chart.tooltip({
                            showTitle: false,
                            showMarkers: false,
                        });

                        chart
                            .interval()
                            .position('percent')
                            .color('item')
                            .label('percent', {
                                content: (data) => {
                                    return `${data.item}: ${data.percent * 100}%`;
                                },
                            })
                            .adjust('stack');

                        chart.interaction('element-active');

                        chart.render();

                        clearTimeout(timer1)

                    }, 0);

                    let timer2 = setTimeout(() => {
                        const data = results.ofSuccessRate;

                        const chart = new Chart({
                            container: 'order-finish-rate',
                            autoFit: true,
                            height: 300,
                            width: 630

                        });

                        chart.coordinate('theta', {
                            radius: 0.75,
                        });

                        chart.data(data);

                        chart.scale('percent', {
                            formatter: (val) => {
                                val = val * 100 + '%';
                                return val;
                            },
                        });

                        chart.tooltip({
                            showTitle: false,
                            showMarkers: false,
                        });

                        chart
                            .interval()
                            .position('percent')
                            .color('item')
                            .label('percent', {
                                content: (data) => {
                                    return `${data.item}: ${data.percent * 100}%`;
                                },
                            })
                            .adjust('stack');

                        chart.interaction('element-active');

                        chart.render();

                        clearTimeout(timer2)

                    }, 0);

                })
            })
    }


    render() {
        const ofDetail = this.state.results.ofDetail
        console.log(ofDetail, this.state.results)
        return (
            <div className="contentWraper">
                <div className="site-card-wrapper ">
                    <Row gutter={16}>
                        <Col span={6}>
                            <Card
                                className="data-card"
                                title={`今日新增：${(undefined === ofDetail) ? 0 : ofDetail[0].newSales}元`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总销售额：<br />
                                    <div className='Card_data'>
                                        {`${(undefined === ofDetail) ? 0 : ofDetail[0].totalSales}`}元
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card
                                title={`今日新增：${(undefined === ofDetail) ? 0 : ofDetail[1].newWillDelivery}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总待发货数：<br />
                                    <div className='Card_data'>
                                        {`${(undefined === ofDetail) ? 0 : ofDetail[1].totalWillDelivery}`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card
                                title={`今日新增：${(undefined === ofDetail) ? 0 : ofDetail[2].newRefunded}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总退款数：<br />
                                    <div className='Card_data'>
                                        {`${(undefined === ofDetail) ? 0 : ofDetail[2].totalRefunded}`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card
                                title={`今日新增：${(undefined === ofDetail) ? 0 : ofDetail[3].newSuccess}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    成交笔数：<br />
                                    <div className='Card_data'>
                                        {`${(undefined === ofDetail) ? 0 : ofDetail[3].totalSuccess}`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                    </Row>
                </div>
                <div className="chart-rule">
                    <div className="chart-wraper">
                        <div className="chart-title" >订单情况概要</div>
                        <div id="order-from-details"></div>
                    </div>
                    <div className="chart-wraper">
                        <div className="chart-title">订单成功率</div>
                        <div id="order-finish-rate"></div>
                    </div>
                </div>
            </div>

        )
    }
}
