import React, { Component } from 'react'
import { Card, Divider, Input, Popconfirm, Button } from 'antd';
import { EditOutlined, CloseOutlined } from '@ant-design/icons';
import reqwest from 'reqwest';
import './index.css'

export default class CommodityCard extends Component {
    state = {
        data: [],
        isEdit: false
    }

    edit = () => {
        this.setState({
            isEdit: true
        })

    }

    delete = (id) => {
        console.log(id)
        this.props.renderCommodityList(id)
    }

    comfimEdit = () => {
        
    }

    propertyEdit = (keyword) => {
        return (isEdit) ? <Input className='editInput' defaultValue={item[keyword]} ref={elev => {this.idElev = elev}} /> : item.id
    }

    componentDidMount = () => {
        this.setState({
            data: this.props.data
        })
    }

    render() {
        const item = this.state.data
        const isEdit = this.state.isEdit
        return (
            <Card
                style={{ width: 300 }}
                cover={
                    <img
                        alt="example"
                        src={item.src}
                    />
                }
                actions={[
                    (isEdit) ? <Button type='primary' size="small" style={{borderRadius:5}} onClick={this.comfimEdit}>确认</Button> : <EditOutlined key="edit" onClick={this.edit} />,
                    <Popconfirm
                        title="确定删除该商品吗?"
                        onConfirm={this.delete.bind(this, item.id)}
                        okText="确认"
                        cancelText="取消"
                    >
                        <CloseOutlined key="ellipsis" />
                    </Popconfirm>
                ]}
            // loading={true}
            >
                <p style={{ margin: '10PX 0 0 0 ', fontWeight: 900, fontSize: 18 }}>{item.commodityName}</p>
                <div className='detailWraper'>
                    <div className='detail'>序号: {(isEdit) ? <Input className='editInput' defaultValue={item.id} ref={elev => {this.idElev = elev}} /> : item.id} </div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>现价: {(isEdit) ? <Input className='editInput' defaultValue={item.currentPrice} /> : item.currentPrice}元</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>库存: {(isEdit) ? <Input className='editInput' defaultValue={item.inventory} /> : item.inventory}件 </div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>规格: {(isEdit) ? <Input className='editInput' defaultValue={item.weight} /> : item.weight}g </div>
                </div>
                <div className='detailWraper'>
                    <div className='detail'>分类: {(isEdit) ? <Input className='editInput' defaultValue={item.category} /> : item.category} </div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>原价: {(isEdit) ? <Input className='editInput' defaultValue={item.originalPrice} /> : item.originalPrice}元 </div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>销量: {(isEdit) ? <Input className='editInput' defaultValue={item.salesVolume} /> : item.salesVolume}件 </div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>产地: {(isEdit) ? <Input className='editInput' defaultValue={item.originPlace} /> : item.originPlace} </div>
                </div>
            </Card>
        )
    }
}
