import React, { Component } from 'react'
import { Descriptions, Divider, Breadcrumb, Table, Button, List, message, Avatar, Spin } from 'antd';
import { Link } from 'react-router-dom'
import reqwest from 'reqwest';
import InfiniteScroll from 'react-infinite-scroller';
import { nanoid } from 'nanoid'
import './index.css'

export default class Category extends Component {

    state = {
        data: [],
        loading: false,
        hasMore: true,
        total: ''
    };

    componentDidMount() {
        this.fetchData(res => {
            this.setState({
                data: res.results,
                total: res.total
            });
        });
    }

    fetchData = callback => {
        reqwest({
            url: '/category/categorylist',
            type: 'json',
            method: 'get',
            contentType: 'application/json',
            success: res => {
                callback(res);
            },
        });
    };

    handleInfiniteOnLoad = () => {
        let { data, total } = this.state;
        this.setState({
            loading: true,
        });
        if (data.length >= total) {
            message.warning('分类已展示完毕');
            this.setState({
                hasMore: false,
                loading: false,
            });
            return;
        }
        this.fetchData(res => {
            data = data.concat(res.results);
            this.setState({
                data,
                loading: false,
            });
        });
    };

    render() {
        return (
            <div>
                <Divider style={{ margin: 0 }} />
                <div className="descwraper">
                    <Breadcrumb className="bdc">
                        <Breadcrumb.Item>
                            <a href="/home">主页</a>
                        </Breadcrumb.Item>
                        <Breadcrumb.Item>商品分类管理</Breadcrumb.Item>
                    </Breadcrumb>
                    <Descriptions title="分类管理" className="desc">
                        <Descriptions.Item>商品分类展示，新增分类，删除分类</Descriptions.Item>
                    </Descriptions>
                    <Divider style={{ margin: 0 }} />
                    <Button
                        type="primary"
                        style={{ marginBottom: 10, marginTop: 10, borderRadius: 5 }}
                    >
                        <Link to='/home/adm/addad'>新增分类</Link>
                    </Button>

                </div>
                <div className="contentWraper">
                    <div className="demo-infinite-container">
                        <InfiniteScroll
                            initialLoad={false}
                            pageStart={0}
                            loadMore={this.handleInfiniteOnLoad}
                            hasMore={!this.state.loading && this.state.hasMore}
                            useWindow={false}
                        >
                            <List
                                dataSource={this.state.data}
                                renderItem={item => (
                                    <List.Item key={nanoid()}>
                                        <List.Item.Meta
                                            title={item.categoryName}
                                        />
                                        <Button type="primary" danger>删除分类</Button>
                                    </List.Item>
                                )}
                            >
                                {this.state.loading && this.state.hasMore && (
                                    <div className="demo-loading-container">
                                        <Spin />
                                    </div>
                                )}
                            </List>
                        </InfiniteScroll>
                    </div>
                </div>
            </div>
        )
    }
}
