import React, { Component } from 'react'
import { Form, Input, Button, message, Popconfirm } from 'antd';
import './index.css'
import reqwest from 'reqwest';


const layout = {
    labelCol: { span: 9 },
    wrapperCol: { span: 5 },
};
const tailLayout = {
    wrapperCol: { offset: 10, span: 16 },
};
const onFinish = (predata, newdata) => {
    console.log(newdata);
    if (newdata.modifiedpassword === predata.password) {
        message.error("密码未修改")
    } else {
        reqwest({
            // 后端接口
            url: '/modifypassword',
            method: 'post',
            type: 'json',
            // 传递给后端的数据
            data: newdata,
        })
            .then(res => {
                console.log(res)
            })
    }

};

export default class UimChangePassword extends Component {
    state = {
        data: {}
    }

    componentDidMount = () => {
        this.setState ({
            data: this.props.data[0]
        })
    }

    render() {
        const data = this.state.data
        return (
            <div>
                <Form
                    {...layout}
                    id="changePasswordFrom"
                    name="basic"
                    initialValues={{ remember: true }}
                    size='large'
                    onFinish={onFinish.bind(this, data)}
                >
                    <Form.Item
                        label="ID"
                        name="ID"
                        required={false}
                        initialValue={`${data.ID}`}
                        hidden
                    >
                    </Form.Item>
                    <Form.Item
                        label="用户名称"
                        name="username"
                        required={false}
                        initialValue={`${data.account}`}
                    >
                        {data.account}
                    </Form.Item>

                    <Form.Item
                        label="用户密码"
                        name="modifiedpassword"
                        required={false}
                        initialValue={`${data.password}`}
                    >
                        <Input  />
                    </Form.Item>

                    <Form.Item {...tailLayout}>
                            <Button
                                type="primary"
                                htmlType="submit"
                                style={{ borderRadius: 5 }}
                            >
                                提交修改
                            </Button>
                    </Form.Item>
                </Form>
            </div>
        )
    }
}
