import React, { Component, } from 'react'
import { Descriptions, Divider, Breadcrumb, Table } from 'antd';
import reqwest from 'reqwest';
import axios from 'axios';
import { Link } from 'react-router-dom'
import 'antd/dist/antd.css'
import './index.css'

const columns = [
    {
        title: 'Name',
        dataIndex: 'name',
        sorter: true,
        render: name => `${name.first} ${name.last}`,
        width: '20%',
    },
    {
        title: 'Gender',
        dataIndex: 'gender',
        filters: [
            { text: 'Male', value: 'male' },
            { text: 'Female', value: 'female' },
        ],
        width: '20%',
    },
    {
        title: 'Email',
        dataIndex: 'email',
    },
];

const getRandomuserParams = params => ({
    results: params.pagination.pageSize,
    page: params.pagination.current,
    ...params,
});

export default class Uim extends Component {
    state = {
        data: [],
        pagination: {
            current: 1,
            pageSize: 10,
        },
        loading: false,
    };

    componentDidMount() {
        const { pagination } = this.state;
        this.fetch({ pagination });
    }

    handleTableChange = (pagination, filters, sorter) => {
        this.fetch({
            sortField: sorter.field,
            sortOrder: sorter.order,
            pagination,
            ...filters,
        });
    };

    fetch = (params = {}) => {
        this.setState({ loading: true });
        // reqwest({
        //     url: 'https://randomuser.me/api',
        //     method: 'get',
        //     type: 'json',
        //     data: getRandomuserParams(params),
        // })
        axios.get('https://randomuser.me/api',getRandomuserParams(params))
        .then(data => {
            console.log(data);
            this.setState({
                loading: false,
                data: data.results,
                pagination: {
                    ...params.pagination,
                    total: 200,
                    // 200 is mock data, you should read it from server
                    // total: data.totalCount,
                },
            });
        });
    };

    render() {
        const { data, pagination, loading } = this.state;
        return (
            <div>
                <Divider style={{ margin: 0 }} />
                <div className="descwraper">
                    <Breadcrumb className="bdc">
                        <Breadcrumb.Item>
                            <a href="/home">主页</a>
                        </Breadcrumb.Item>
                        <Breadcrumb.Item>用户信息管理</Breadcrumb.Item>
                    </Breadcrumb>
                    <Descriptions title="用户列表" className="desc">
                        <Descriptions.Item>用户信息展示，可进行用户密码修改和删除用户操作</Descriptions.Item>
                    </Descriptions>
                </div>

                <div className="contentWraper">
                    <Table
                        columns={columns}
                        rowKey={record => record.login.uuid}
                        dataSource={data}
                        pagination={pagination}
                        loading={loading}
                        onChange={this.handleTableChange}
                    />
                </div>
            </div>
        )
    }
}
