import React, { Component } from 'react'
import { Card, Col, Row, Space, Button } from 'antd';
import { Chart } from '@antv/g2';
import reqwest from 'reqwest'
import './index.css';

export default class Dashboard extends Component {

    state = {
        results:{

        }
    }

    componentDidMount() {
        reqwest({
            url: "/dashboard/showdashboard",
            type:"json",
            method:"get",
        })
            .then(res => {
                this.setState({
                    results:res
                },() => {

                })
            })
    }


    render() {
        return (
            <div className="contentWraper">
                <div className="site-card-wrapper ">
                    <Row gutter={16}>
                        <Col span={6}>
                            <Card
                                className="data-card"
                                title={`今日新增：${341}元`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总销售额：<br />
                                    <div className='Card_data'>
                                        {`3400`}元
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card
                                title={`今日新增：${6}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总待发货数：<br />
                                    <div className='Card_data'>
                                        {`32`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card
                                title={`今日新增：${1}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总退款数：<br />
                                    <div className='Card_data'>
                                        {`40`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card
                                title={`今日新增：${10}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    成交笔数：<br />
                                    <div className='Card_data'>
                                        {`400`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                    </Row>
                </div>
                <div className="chart-rule">
                    <div className="chart-wraper">
                        <div className="chart-title" >订单情况概要</div>
                        <div id="order-from-details"></div>
                    </div>
                    <div className="chart-wraper">
                        <div className="chart-title">订单成功率</div>
                        <div id="order-finish-rate"></div>
                    </div>
                </div>
            </div>

        )
    }
}
