let ofStatusToChinese = (arr) => {
    arr.forEach(item => {
        // console.log(item.status)
        switch (item.status) {
            case "willDelivery":
                item.status = "待发货"
                break;
            case "delivery":
                item.status = "配送中"
                break;
            case "success":
                item.status = "已成交"
                break;
            case "refund":
                item.status = "待退款"
                break;
            case "refunded":
                item.status = "已退款"
                break;
            case "totalSuccess":
                item.status = "总成交数"
                break;
            case "totalRefunded":
                item.status = "总失败数"
                break;
            default:
                return
        }
        switch (item.status) {
            case "willdelivery":
                item.status = "待发货"
                break;
            case "delivery":
                item.status = "配送中"
                break;
            case "success":
                item.status = "已成交"
                break;
            case "refund":
                item.status = "待退款"
                break;
            case "refunded":
                item.status = "已退款"
                break;
            default:
                return
        }
    })
}
export default ofStatusToChinese
