import React, { Component } from 'react'
import { BrowserRouter, Route, Switch, Redirect, Link } from 'react-router-dom';
import { Descriptions, Divider, Breadcrumb, Button, message, Popconfirm ,Table, Tag, Space } from 'antd';
import reqwest from 'reqwest';
import 'antd/dist/antd.css'
import './index.css'

const columns = [
    {
      title: 'ID',
      dataIndex: 'ID',
      key: 'ID',
    },
    {
      title: '广告名称',
      dataIndex: 'adName',
      key: 'adName',
    },
    {
      title: 'Address',
      dataIndex: 'address',
      key: 'address',
    },
    {
      title: 'Tags',
      key: 'tags',
      dataIndex: 'tags',
      render: tags => (
        <>
          {tags.map(tag => {
            let color = tag.length > 5 ? 'geekblue' : 'green';
            if (tag === 'loser') {
              color = 'volcano';
            }
            return (
              <Tag color={color} key={tag}>
                {tag.toUpperCase()}
              </Tag>
            );
          })}
        </>
      ),
    },
    {
      title: 'Action',
      key: 'action',
      render: (text, record) => (
        <Space size="middle">
          <a>Invite {record.name}</a>
          <a>Delete</a>
        </Space>
      ),
    },
  ];
  


export default class Adm extends Component {
    state = {
        adData:''
    }

    componentDidMount () {
        reqwest({
            url:'/showad',
            method:'post',
            type:'json',
            data:''
        })
        .then(res => {
            this.setState({
                adData:res
            })
        })
    }

    render() {
        return (
            <div>
                <Divider style={{ margin: 0 }} />
                <div className="descwraper">
                    <Breadcrumb className="bdc">
                        <Breadcrumb.Item>
                            <a href="/home">主页</a>
                        </Breadcrumb.Item>
                        <Breadcrumb.Item>广告投放管理</Breadcrumb.Item>
                    </Breadcrumb>
                    <Descriptions title="广告管理" className="desc">
                        <Descriptions.Item>广告信息展示，可以进行新增广告，更改广告，删除广告操作。注意：最多只允许3个广告位</Descriptions.Item>
                    </Descriptions>
                </div>
                <div className="contentWraper">
                    {/* <BrowserRouter>
                        <Switch>

                        </Switch>
                    </BrowserRouter> */}
                    <Table columns={columns} dataSource={this.state.adData} />
                </div>
            </div>
        )
    }
}
