import React, { Component } from 'react'
import { Card, Divider, Popconfirm, Space } from 'antd';
import { EditOutlined, CloseOutlined } from '@ant-design/icons';
import reqwest from 'reqwest';
import './index.css'

export default class CommodityCard extends Component {
    state = {
        data: []
    }

    edit = () => {

    }

    delete = (id) => {
        console.log(id)
        reqwest({
            url: '/deletecommodity',
            method: 'post',
            type: 'json',
            data: { id }
        })
            .then(res => {
                this.props.renderCommodityList(res.results)
            })
    }

    componentDidMount = () => {
        this.setState({
            data: this.props.data
        })
    }

    render() {
        const item = this.state.data
        return (
            <Card
                style={{ width: 300 }}
                cover={
                    <img
                        alt="example"
                        src={item.src}
                    />
                }
                actions={[
                    <EditOutlined key="edit" onClick={this.edit} />,
                    <Popconfirm
                        title="确定删除该广告吗?"
                        onConfirm={this.delete(item.id)}
                        okText="确认"
                        cancelText="取消"
                    >
                        <CloseOutlined key="ellipsis" />
                    </Popconfirm>
                ]}
            // loading={true}
            >
                <p style={{ margin: '10PX 0 0 0 ', fontWeight: 900, fontSize: 18 }}>{item.commodityName}</p>
                <div className='detailWraper'>
                    <div className='detail'>序号: {item.id}</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>现价: {item.currentPrice}</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>库存: {item.inventory}</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>规格: {item.weight}</div>
                </div>
                <div className='detailWraper'>
                    <div className='detail'>分类: {item.category}</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>原价: {item.originalPrice}</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>销量: {item.salesVolume}</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>产地: {item.originPlace}</div>
                </div>
            </Card>
        )
    }
}
