import React, { Component } from 'react'
import { Card, Col, Row, Space, Button } from 'antd';
import { Chart } from '@antv/g2';
import reqwest from 'reqwest'
import './index.css';
// 引用工具
import ofStatusToChinese from '../../../utils/ofStatusToChinese';

export default class Dashboard extends Component {

    state = {
        results: {

        }
    }

    componentDidMount() {
        reqwest({
            url: "/dashboard/showdashboard",
            type: "json",
            method: "get",
        })
            .then(res => {
                console.log(res)
                // 处理数据
                let totalof = res.willDelivery + res.delivery + res.success + res.refund + res.refunded
                console.log(res.willDelivery / totalof )
                let ofSummary = [
                    {
                        item: "willDelivery",
                        count: res.willDelivery,
                        percent: res.willDelivery * 1.0 / totalof
                    },
                    {
                        item: "delivery",
                        count: res.delivery,
                        percent: res.delivery * 1.0 / totalof
                    },
                    {
                        item: "success",
                        count: res.success,
                        percent: res.success * 1.0 / totalof
                    },
                    {
                        item: "refund",
                        count: res.refund,
                        percent: res.refund * 1.0 / totalof
                    },
                    {
                        item: "refunded",
                        count: res.refunded,
                        percent: res.refunded * 1.0 / totalof
                    }
                ]
                ofStatusToChinese(ofSummary)
                ofStatusToChinese(res.results.ofSuccessRate)
                this.setState({
                    results: res.results
                }, () => {
                    const results = this.state.results
                    let timer1 = setTimeout(() => {
                        const data = results.ofSummary;

                        const chart = new Chart({
                            container: 'order-from-details',
                            autoFit: true,
                            height: 300,
                            width: 630
                        });

                        chart.coordinate('theta', {
                            radius: 0.75,
                        });

                        chart.data(data);

                        chart.scale('percent', {
                            formatter: (val) => {
                                val = val * 100 + '%';
                                return val;
                            },
                        });

                        chart.tooltip({
                            showTitle: false,
                            showMarkers: false,
                        });

                        chart
                            .interval()
                            .position('percent')
                            .color('item')
                            .label('percent', {
                                content: (data) => {
                                    return `${data.item}: ${data.percent * 100}%`;
                                },
                            })
                            .adjust('stack');

                        chart.interaction('element-active');

                        chart.render();

                        clearTimeout(timer1)

                    }, 0);

                    let timer2 = setTimeout(() => {
                        const data = results.ofSuccessRate;

                        const chart = new Chart({
                            container: 'order-finish-rate',
                            autoFit: true,
                            height: 300,
                            width: 630

                        });

                        chart.coordinate('theta', {
                            radius: 0.75,
                        });

                        chart.data(data);

                        chart.scale('percent', {
                            formatter: (val) => {
                                val = val * 100 + '%';
                                return val;
                            },
                        });

                        chart.tooltip({
                            showTitle: false,
                            showMarkers: false,
                        });

                        chart
                            .interval()
                            .position('percent')
                            .color('item')
                            .label('percent', {
                                content: (data) => {
                                    return `${data.item}: ${data.percent * 100}%`;
                                },
                            })
                            .adjust('stack');

                        chart.interaction('element-active');

                        chart.render();

                        clearTimeout(timer2)

                    }, 0);

                })
            })
    }


    render() {
        const ofDetail = this.state.results.ofDetail
        console.log(ofDetail, this.state.results)
        return (
            <div className="contentWraper">
                <div className="site-card-wrapper ">
                    <Row gutter={16}>
                        <Col span={4}>
                            <Card
                                className="data-card"
                                title={`今日新增：${(undefined === ofDetail) ? 0 : ofDetail.sales.newSales}元`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总销售额：<br />
                                    <div className='Card_data'>
                                        {`${(undefined === ofDetail) ? 0 : ofDetail.sales.totalSales}`}元
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={4}>
                            <Card
                                title={`今日新增：${(undefined === ofDetail) ? 0 : ofDetail.willDelivery.newwillDelivery}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总待发货数：<br />
                                    <div className='Card_data'>
                                        {`${(undefined === ofDetail) ? 0 : ofDetail.willDelivery.totalwillDelivery}`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={4}>
                            <Card
                                title={`今日新增：${(undefined === ofDetail) ? 0 : ofDetail.delivery.newDelivery}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总配送中数：<br />
                                    <div className='Card_data'>
                                        {`${(undefined === ofDetail) ? 0 : ofDetail.delivery.totalDelivery}`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={4}>
                            <Card
                                title={`今日新增：${(undefined === ofDetail) ? 0 : ofDetail.refund.newRefund}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总待退款数：<br />
                                    <div className='Card_data'>
                                        {`${(undefined === ofDetail) ? 0 : ofDetail.refund.totalRefund}`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={4}>
                            <Card
                                title={`今日新增：${(undefined === ofDetail) ? 0 : ofDetail.refunded.newRefunded}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    总退款数：<br />
                                    <div className='Card_data'>
                                        {`${(undefined === ofDetail) ? 0 : ofDetail.refunded.totalRefunded}`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                        <Col span={4}>
                            <Card
                                title={`今日新增：${(undefined === ofDetail) ? 0 : ofDetail.success.newSuccess}`}
                                bordered={true}
                            >
                                <div className="dataCard">
                                    成交笔数：<br />
                                    <div className='Card_data'>
                                        {`${(undefined === ofDetail) ? 0 : ofDetail.success.totalSuccess}`}
                                    </div>
                                </div>
                            </Card>
                        </Col>
                    </Row>
                </div>
                <div className="chart-rule">
                    <div className="chart-wraper">
                        <div className="chart-title" >订单情况概要</div>
                        <div id="order-from-details"></div>
                    </div>
                    <div className="chart-wraper">
                        <div className="chart-title">订单转化率</div>
                        <div id="order-finish-rate"></div>
                    </div>
                </div>
            </div>

        )
    }
}
