import React, { Component } from 'react'
import { Form, Input, Button, message, PageHeader } from 'antd';
import './AAindex.css'
import reqwest from 'reqwest';

const layout = {
    labelCol: { span: 9 },
    wrapperCol: { span: 5 },
};
const tailLayout = {
    wrapperCol: { offset: 10, span: 16 },
};

export default class AdmAddAd extends Component {

    onFinish = (predata, newdata) => {
        if (newdata.modifiedpassword === predata.password) {
            message.warning("密码未修改！")
        } else {
            reqwest({
                // 后端接口
                url: '/modifypassword',
                method: 'post',
                type: 'json',
                // 传递给后端的数据
                data: newdata,
            })
                .then(res => {
                    console.log(res)
                    message.success("密码修改成功！")
                }, () => {
                    message.error("密码修改失败！")
                })
        }
    };

    render() {
        const data = ''
        return (
            <div>
                <PageHeader
                    className="site-page-header"
                    onBack={() => { window.location.href = '/home/adm' }}
                    subTitle="广告投放管理/新增广告"
                    style={{ paddingLeft: 10, backgroundColor: 'white' }}
                />
                <Form
                    {...layout}
                    name="basic"
                    initialValues={{ remember: true }}
                    onFinish={onFinish}
                >
                    <Form.Item
                        label="Username"
                        name="username"
                        rules={[{ required: true, message: 'Please input your username!' }]}
                    >
                        <Input />
                    </Form.Item>

                    <Form.Item
                        label="Password"
                        name="password"
                        rules={[{ required: true, message: 'Please input your password!' }]}
                    >
                        <Input.Password />
                    </Form.Item>

                    <Form.Item {...tailLayout} name="remember" valuePropName="checked">
                        <Checkbox>Remember me</Checkbox>
                    </Form.Item>

                    <Form.Item {...tailLayout}>
                        <Button type="primary" htmlType="submit">
                            Submit
                        </Button>
                    </Form.Item>
                </Form>
            </div>
        )
    }
}
