import React, { Component } from 'react'
import { Card, Divider, Space } from 'antd';
import { EditOutlined, CloseOutlined } from '@ant-design/icons';
import './index.css'

export default class CommodityCard extends Component {

    edit = () => {
        
    }

    render() {
        return (
            <Card
                style={{ width: 300 }}
                cover={
                    <img
                        alt="example"
                        src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
                    />
                }
                actions={[
                    <EditOutlined key="edit" onClick={this.edit} />,
                    <CloseOutlined key="ellipsis" onClick={this.delete} />,
                ]}
            // loading={true}
            >
                <div className='detailWraper'>
                    <div className='detail'>序号:XXXXXXXXXXX</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>现价:XXXXX</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>库存:XXXXX</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>规格:</div>
                </div>
                <div className='detailWraper'>
                    <div className='detail'>序号:XXXXXXXXXXX</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>现价:XXXXX</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>库存:XXXXX</div>
                    <Divider style={{ margin: 0 }} />
                    <div className='detail'>规格:</div>
                </div>
            </Card>
        )
    }
}
