import React, { Component } from 'react'
import { Descriptions, Divider, Breadcrumb, Button, Input, message, InputNumber, DatePicker, Table, ConfigProvider, Col, Row, Space, Affix } from 'antd';
import { BrowserRouter, Link, Switch, Route } from 'react-router-dom'
import reqwest from 'reqwest';
import InfiniteScroll from 'react-infinite-scroller';
import { SearchOutlined } from '@ant-design/icons'
import './index.css'
// 配置中文环境
import zh_CN from 'antd/lib/locale-provider/zh_CN';
import 'moment/locale/zh-cn';

const { RangePicker } = DatePicker;

const columns = [
    { title: 'Name', dataIndex: 'name', key: 'name' },
    { title: 'Age', dataIndex: 'age', key: 'age' },
    { title: 'Address', dataIndex: 'address', key: 'address' },
];

const data = [
    {
        key: 1,
        name: 'John Brown',
        age: 32,
        address: 'New York No. 1 Lake Park',
        description: 'My name is John Brown, I am 32 years old, living in New York No. 1 Lake Park.',
    },
    {
        key: 2,
        name: 'Jim Green',
        age: 42,
        address: 'London No. 1 Lake Park',
        description: 'My name is Jim Green, I am 42 years old, living in London No. 1 Lake Park.',
    }
];


export default class Om extends Component {

    state = {
        date: [],
        data: [],
        loading: false,
        hasMore: true,
        total: '',
        isAddCategory: false,
        reqCount: 0
    };

    dateOnChange = (value, dateString) => {
        console.log('Formatted Selected Time: ', dateString);
        this.setState({
            date: dateString
        })
    }

    OmSearch = () => {
        console.log(this.state.date)
        let [oid, ofName, date] = [
            this.idInput.value,
            this.nameInput.state.value,
            this.state.date
        ]
        const data = { oid, ofName, date }
        console.log(data)
        reqwest({
            url: 'searchorderform',
            method: 'post',
            type: 'json',
            data: data
        })
    }

    handleInfiniteOnLoad = () => {
        let { data, total } = this.state;
        this.setState({
            loading: true,
        });
        if (data.length >= total) {
            message.warning('分类已展示完毕');
            this.setState({
                hasMore: false,
                loading: false,
            });
            return;
        }
        this.fetchData(res => {
            data = data.concat(res.results);
            this.setState({
                data,
                loading: false,
                total: res.total
            });
        });
    };

    fetchData = callback => {
        let reqCount = this.state.reqCount
        reqCount += 1
        this.setState({
            reqCount:reqCount
        })
        let data = {reqCount}
        data = JSON.stringify(data)
        reqwest({
            url: '/category/categorylist',
            type: 'json',
            method: 'post',
            data:data,
            contentType: 'application/json',
            success: res => {
                callback(res);
            },
        });
    };

    componentDidMount() {
        this.fetchData(res => {
            this.setState({
                data: res.results,
                total: res.total
            });
        });
    }

    render() {
        return (
            <div>
                <Divider style={{ margin: 0 }} />
                <div className="Om-head">
                    <div className="Om-desc-wraper">
                        <Breadcrumb className="bdc">
                            <Breadcrumb.Item>
                                <a href="/home">主页</a>
                            </Breadcrumb.Item>
                            <Breadcrumb.Item>订单信息管理</Breadcrumb.Item>
                        </Breadcrumb>
                        <Descriptions title="订单管理" className="desc">
                            <Descriptions.Item>展示订单信息，查询订单，查看订单状态，处理订单的发货，确认送达和退款</Descriptions.Item>
                        </Descriptions>
                    </div>
                    <div className="status-switch">
                        <Space>
                            <Button type="primary" className="status-button sendgoods">待发货</Button>
                            <Button type="primary" className="status-button delivery">配送中</Button>
                            <Button type="primary" className="status-button success">已成功</Button>
                            <Button type="primary" className="status-button refund">退款中</Button>
                            <Button type="primary" className="status-button refunded">已退款</Button>
                        </Space>
                    </div>
                </div>
                <Affix>
                    <div className="search">
                        <Divider style={{ margin: 0 }} />
                        <Space style={{ marginTop: 20 }} size={20}>
                            <div>
                                订单id:
                                <InputNumber name='Oid' className="Om-input" ref={elev => { this.idInput = elev }} />
                            </div>
                            <div>
                                用户名称:
                                <Input name="commodity" className="Om-input" ref={elev => { this.nameInput = elev }} />
                            </div>
                            <div>
                                时间:
                                <ConfigProvider locale={zh_CN}>
                                    <RangePicker
                                        showTime={{ format: 'HH:mm' }}
                                        format="YYYY-MM-DD HH:mm"
                                        onChange={this.dateOnChange}
                                        ref={elev => { this.dateElev = elev }}
                                    />
                                </ConfigProvider>
                            </div>
                            <div style={{ marginLeft: 20 }}>
                                <Button type="primary" shape="circle" icon={<SearchOutlined />} onClick={this.OmSearch} />
                            </div>
                        </Space>
                    </div>
                </Affix>
                <div className="contentWraper">
                    <InfiniteScroll
                        initialLoad={false}
                        pageStart={0}
                        loadMore={this.handleInfiniteOnLoad}
                        hasMore={!this.state.loading && this.state.hasMore}
                        useWindow={false}
                    >
                        <Table
                            columns={columns}
                            expandable={{
                                expandedRowRender: record => <p style={{ margin: 0 }}>{record.description}</p>,
                            }}
                            dataSource={data}
                            pagination={{
                                pageSize: 100000,
                                hideOnSinglePage: true,
                                responsive: true
                            }}
                            scroll={{ y: 410 }}
                        />
                    </InfiniteScroll>
                </div>
                </div>

        )
    }
}
