import React, { Component, } from 'react'
import { Descriptions, Divider, Breadcrumb, Table, Button, message, Popconfirm } from 'antd';
import reqwest from 'reqwest';
import axios from 'axios';
import { BrowserRouter, Link, Route, Switch, } from 'react-router-dom'
import 'antd/dist/antd.css'
import './index.css'
import UimChangePassword from './UimChangePassword'


const getRandomuserParams = params => ({
    page: params.pagination.current,
    pageSize: params.pagination.pageSize,
    ...params,
});

export default class Uim extends Component {
    state = {
        data: [],
        pagination: {
            current: 1,
            pageSize: 10
        },
        loading: false,
        status: 0
    };

    confirm(ID) {
        let { current, pageSize } = this.state.pagination
        const data = { ID: ID, current, pageSize}
        reqwest({
            // 后端接口
            url: '/deletuser',
            method: 'post',
            type: 'json',
            // 传递给后端的数据
            data: data,
        })
            .then(res => {
                console.log(res)
                this.setState({
                    data: res
                })
                message.success('删除成功');
            }, () => {
                message.error('删除失败');
            })
    }

    columns = [
        {
            title: 'ID',
            dataIndex: 'ID',
            defaultSortOrder: 'ascend',
            sorter: (a, b) => a.ID - b.ID,
            width: '20%',
        },
        {
            title: '账号',
            dataIndex: 'account',
            width: '20%',
        },
        {
            title: '密码',
            dataIndex: 'password',
            width: '20%',
            editable: true
        },
        {
            title: '操作',
            dataIndex: 'ID',
            render: (ID) => {
                return (
                    <>
                        <Button
                            type="primary"
                            style={{ borderRadius: 5 }}
                        >
                            <Link to={`/home/changepassword/${ID}`}>
                                更改密码
                            </Link>
                        </Button>
                        <Button
                            type="primary"
                            style={{ borderRadius: 5, marginLeft: 20 }}
                            danger
                        >
                            <Popconfirm
                                title="确定删除该用户吗?"
                                onConfirm={this.confirm.bind(this, ID)}
                                okText="确认"
                                cancelText="取消"
                            >
                                删除用户
                            </Popconfirm>
                        </Button>
                    </>
                )
            }
        },
    ];

    componentDidMount() {
        const { pagination } = this.state;
        this.fetch({ pagination });
    }

    handleTableChange = (pagination) => {
        console.log(pagination)
        let { current, pageSize } = pagination
        // console.log(pagination)
        this.setState({
            pagination: {
                current,
                pageSize
            }
        })
        this.fetch({
            pagination,
        });
    };

    fetch = (params = {}) => {
        this.setState({ loading: true });
        reqwest({
            // 后端接口
            url: '/userlist',
            method: 'post',
            type: 'json',
            // 传递给后端的数据
            data: getRandomuserParams(params),
        })
            .then(data => {
                // console.log(data);
                this.setState({
                    loading: false,
                    // 根据接口返回的数据源
                    data: data.results,
                    pagination: {
                        ...params.pagination,
                        // 根据接口返回的总条数
                        total: data.total,
                    },
                });
            })
            ;
    };

    UimChangePasswordComponent = (props) => {
        let ID = props.match.params.ID
        let dataitem = this.state.data.filter(item => {
            if (item.ID == ID) {
                return item
            }
        })
        return <UimChangePassword data={dataitem} />
    }

    render() {
        const { data, pagination, loading } = this.state;
        function TableComponent() {
            return <Table
                columns={this.columns}
                rowKey={record => record.login}
                dataSource={data}
                pagination={pagination}
                loading={loading}
                onChange={this.handleTableChange}
            />
        }
        return (
            <div>
                <Divider style={{ margin: 0 }} />
                <div className="descwraper">
                    <Breadcrumb className="bdc">
                        <Breadcrumb.Item>
                            <a href="/home">主页</a>
                        </Breadcrumb.Item>
                        <Breadcrumb.Item>用户信息管理</Breadcrumb.Item>
                    </Breadcrumb>
                    <Descriptions title="用户列表" className="desc">
                        <Descriptions.Item>用户信息展示，可进行用户密码修改和删除用户操作</Descriptions.Item>
                    </Descriptions>
                </div>
                <div className="contentWraper">
                    <BrowserRouter>
                        <Switch>
                            <Route path='/home/changepassword/:ID?' component={this.UimChangePasswordComponent} />
                            <Route path='/home/uim' component={TableComponent.bind(this)} />
                        </Switch>
                    </BrowserRouter>
                </div>
            </div>
        )
    }
}
