import React, { Component } from 'react'
import { Form, Input, Button } from 'antd';
import './index.css'
import reqwest from 'reqwest';


const layout = {
    labelCol: { span: 10 },
    wrapperCol: { span: 5 },
};
const tailLayout = {
    wrapperCol: { offset: 11, span: 16 },
};
const onFinish = (values) => {
    console.log('Success:', values);
  };

export default class UimChangePassword extends Component {
    state = {
        data: {}
    }

    componentWillMount = () => {
        this.setState({
            data: this.props.data[0]
        })
    }

    commitModification = (e) => {
        console.log(e)

    }

    render() {
        console.log()
        let data = this.state.data
        console.log(data)

        return (
            <div>
                <Form
                    {...layout}
                    id="changePasswordFrom"
                    name="basic"
                    initialValues={{ remember: true }}
                    size='large'
                    onFinish={onFinish}
                >
                    <Form.Item
                        label="用户名称"
                        name="username"
                        required={false}
                        initialValue={`${data.account}`}
                        hidden
                    >
                        {data.account}
                    </Form.Item>

                    <Form.Item
                        label="用户密码"
                        name="password"
                        required={false}
                    >
                        <Input defaultValue={`${data.password}`} />
                    </Form.Item>

                    <Form.Item {...tailLayout}>
                        <Button
                            type="primary"
                            htmlType="submit"
                            style={{ borderRadius: 5 }}
                            onClick={this.commitModification}
                        >
                            提交修改
                        </Button>
                    </Form.Item>
                </Form>
            </div>
        )
    }
}
