import React, { Component } from 'react'
import { Form, Input, Button, message, PageHeader } from 'antd';
import './AAindex.css'
import reqwest from 'reqwest';

const layout = {
    labelCol: { span: 9 },
    wrapperCol: { span: 5 },
};
const tailLayout = {
    wrapperCol: { offset: 10, span: 16 },
};

export default class AdmAddAd extends Component {
    render() {
        return (
            <div>
                <PageHeader
                    className="site-page-header"
                    onBack={() => { window.location.href = '/home/uim' }}
                    subTitle="用户信息管理/修改用户密码"
                    style={{ paddingLeft: 10, backgroundColor: 'white' }}
                />
                <Form
                    {...layout}
                    id="changePasswordFrom"
                    name="basic"
                    initialValues={{ remember: true }}
                    size='large'
                    onFinish={this.onFinish.bind(this, data)}
                >
                    <Form.Item
                        label="ID"
                        name="ID"
                        required={false}
                        initialValue={`${data.ID}`}
                        hidden
                    >
                        <Input hidden />
                    </Form.Item>
                    <Form.Item
                        label="用户名称"
                        name="username"
                        required={false}
                        initialValue={`${data.account}`}
                    >
                        <Input hidden />
                        {data.account}
                    </Form.Item>

                    <Form.Item
                        label="用户密码"
                        name="modifiedpassword"
                        required={false}
                        initialValue={`${data.password}`}
                    >
                        <Input.Password />
                    </Form.Item>

                    <Form.Item {...tailLayout}>
                        <Button
                            type="primary"
                            htmlType="submit"
                            style={{ borderRadius: 5 }}
                        >
                            提交修改
                        </Button>
                    </Form.Item>
                </Form>
            </div>
        )
    }
}
