import React, { Component, } from 'react'
import { Descriptions, Divider, Breadcrumb, Table, Button  } from 'antd';
import reqwest from 'reqwest';
import axios from 'axios';
import { Link } from 'react-router-dom'
import 'antd/dist/antd.css'
import './index.css'
import { CodeSandboxCircleFilled } from '@ant-design/icons';

const columns = [
    {
        title: 'ID',
        dataIndex: 'ID',
        sorter: true,
        width: '20%',
    },
    {
        title: '账号',
        dataIndex: 'account',
        width: '20%',
    },
    {
        title: '密码',
        dataIndex: 'password',
        width: '20%',

    },
    {
        title: '操作',
        dataIndex: 'ID',
        reder:(e) => {
            console.log(e)
            return  
        }
    },
];

const getRandomuserParams = params => ({
    page: params.pagination.current,
    pageSize: params.pagination.pageSize,
    ...params,
});

export default class Uim extends Component {
    state = {
        data: [],
        pagination: {
            current: 1,
            pageSize: 10
        },
        loading: false,
    };

    componentDidMount() {
        const { pagination } = this.state;
        this.fetch({ pagination });
    }

    handleTableChange = (pagination) => {
        console.log(pagination)
        this.fetch({
            pagination,
        });
    };

    fetch = (params = {}) => {
        this.setState({ loading: true });
        reqwest({
            // 后端接口
            url: '/userlist',
            method: 'post',
            type: 'json',
            // 传递给后端的数据
            data: getRandomuserParams(params),
        })
        .then(data => {
            console.log(data);
            this.setState({
                loading: false,
                // 根据接口返回的数据源
                data: data.results,
                pagination: {
                    ...params.pagination,
                    // 根据接口返回的总条数
                    total: 100,
                },
            });
        }).then(() => {
            console.log(this.state.pagination)
        })
        ;
    };

    render() {
        const { data, pagination, loading } = this.state;
        return (
            <div>
                <Divider style={{ margin: 0 }} />
                <div className="descwraper">
                    <Breadcrumb className="bdc">
                        <Breadcrumb.Item>
                            <a href="/home">主页</a>
                        </Breadcrumb.Item>
                        <Breadcrumb.Item>用户信息管理</Breadcrumb.Item>
                    </Breadcrumb>
                    <Descriptions title="用户列表" className="desc">
                        <Descriptions.Item>用户信息展示，可进行用户密码修改和删除用户操作</Descriptions.Item>
                    </Descriptions>
                </div>
                <div className="contentWraper">
                    <Table
                        columns={columns}
                        rowKey={record => record.login}
                        dataSource={data}
                        pagination={pagination}
                        loading={loading}
                        onChange={this.handleTableChange}
                    />
                </div>
            </div>
        )
    }
}
