import React, { Component, Fragment } from 'react'
import { Descriptions, Divider, Breadcrumb, Button, Input, message, InputNumber, Table, Tag, Select, Dropdown, Space } from 'antd';
import { DownOutlined, UserOutlined, SearchOutlined } from '@ant-design/icons'
import './index.css'
import reqwest from 'reqwest';

const { Option } = Select;

export default class Cim extends Component {
    state = {
        CommodityData:[]
    }

    CimSearch = () => {
        const [id, commodity, category, status] = [
            this.idInput.value,
            this.nameInput.state.value,
