import React, { Component } from 'react'
import { Form, Input, Button, Checkbox } from 'antd';
import './index.css'

const layout = {
    labelCol: { span: 10 },
    wrapperCol: { span: 5 },
};
const tailLayout = {
    wrapperCol: { offset: 11, span: 16 },
};

export default class UimChangePassword extends Component {
    state = {
        data: {}
    }

    componentWillMount = () => {
        this.setState({
            data: this.props.data[0]
        })
    }

    render() {
        console.log()
        let data = this.state.data
        console.log(data)

        return (
            <div>
                <Form
                    {...layout}
                    id="changePasswordFrom"
                    name="basic"
                    initialValues={{ remember: true }}
                    size='large'
                >
                    <Form.Item
                        label="用户名称"
                        name="username"
                        rules={[{ required: true, message: 'Please input your username!' }]}
                        required={false}
                    >
                        {data.account}
                    </Form.Item>

                    <Form.Item
                        label="用户密码"
                        name="password"
                        rules={[{ required: true, message: 'Please input your password!' }]}
                        required={false}
                    >
                        <Input defaultValue={`${data.password}`} />
                    </Form.Item>

                    <Form.Item {...tailLayout}>
                        <Button
                         type="primary" htmlType="submit" style={{borderRadius:5}}>
                            确认修改
                        </Button>
                    </Form.Item>
                </Form>
            </div>
        )
    }
}
